sys_ux_client_script.script:
/**
* @param {params} params
* @param {api} params.api
* @param {any} params.event
* @param {any} params.imports
* @param {ApiHelpers} params.helpers
*/
function onDataBinding(context) {
  const subscriptions = context.inputs.subscriptions || [];
  const subscribedCourseIds = subscriptions.map(sub => sub.course.value);
  const learnerId = context.inputs.currentLearner.record.sys_id;

  return {
    subscribedCourseIds,
    selectedLearnerId: learnerId
  };
}
